import React from "react";
import { ScrollView, Text, View, TextInput } from "react-native";

export function UseReducerScreen(state, action) {
    switch (action.type) {
        case 'first':
          return { ...state, first: action.value }
        case 'last':
          return { ...state, last: action.value }
      }
    }

    export  function App() {
        const [state, dispatch] = useReducer(reducer, { first: '', last: '' })
      
        return (
          <View>
            <TextInput
              style={{ fontSize: 32 }}
              placeholder="First"
              value={state.first}
              onChangeText={(text) => {
                dispatch({ type: 'first', value: text })
              }}
            />
            <TextInput
              style={{ fontSize: 32 }}
              placeholder="Last"
              value={state.last}
              onChangeText={(text) => {
                dispatch({ type: 'last', value: text })
              }}
            />
            <Text style={{ fontSize: 32 }}>
              Hello {state.first} {state.last}
            </Text>
          </View>
        )
      }
